from openai import OpenAI

client = OpenAI()

response = client.chat.completions.create(
  model="gpt-4-vision-preview",
  messages=[
    {
      "role": "user",
      "content": [
        {"type": "text", "text": "この画像の中の文字を出力してください"},
        {
          "type": "image_url",
          "image_url": {
            "url": "data:image/png;base64,",
          },
        },
      ],
    }
  ],
  max_tokens=300,
)

print(response.choices[0])